import React from 'react';
import './MyPage.css';
import { bindExpression } from '@babel/types';


class MyPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            pending: ['hoc', 'ngu', 'an', 'boi', 'boi'],
            doing: ['nau com', 'coding', 'singing'],
            done: [],
            added: '',

        };
        this.addItem = this.addItem.bind(this);
        this.onChangeValue = this.onChangeValue.bind(this);
    }

    onChangeValue(e) {
        this.setState({added: e.target.value});
    }

    addItem() {
       const pending = this.state.pending.concat(this.state.added);
       return {
           pending
       };

    }






    render() {
        return (
            <div>
                Hello Tram
                <div class="todoContainer">
                    <ul class="list-group" id="PendingList">
                        <li class="list-group-item">Haha</li>
                        <li class="list-group-item">Script</li>
                        <li class="list-group-item active" >JAva</li>
                        <li class="list-group-item">Hello</li>
                        <li class="list-group-item">Lam</li>
                    </ul>
                    <ul>
                        {this.state.pending.map(item => (
                            <li key={item}>{item}</li>
                        ))}
                    </ul>
                    <input type="text" id="enterItem" placeholder="..." onChange={this.onChangeValue} />
                    <span id="addToDo" onClick={this.addItem}>Add</span>
                </div>

            </div>

        );
    }
}

export default MyPage;
