import React from 'react';
import './MyPage.css';



class MyPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            pending: [
                {id: 1, value: 'Hoc'},
                {id: 2, value: 'Ngu'},
                {id: 3, value: 'The thao'},
            ]    ,    //   List co san
            added: {id: '', value: ''}  ,  // gia tri them
            index: 3

        };
        this.addItem = this.addItem.bind(this);
        this.onChangeValue = this.onChangeValue.bind(this);

    }

    onChangeValue(e) {
        let oldIndex = this.state.index;
        let newIndex = oldIndex + 1;
        this.setState({index: newIndex, added: {id: newIndex, value: e.target.value} });
    }

    addItem() {
       this.setState(state => {
        const pending = state.pending.concat(state.added);
        return {
            pending,        
        };
       });         
    }

    removeItem = id => {
       // console.log(id);
        this.setState(state => {
            const pending = state.pending.filter(item => (item.id !== id));
            return {
                pending,
            };
        });
    }


    render() {
        return (
            <div>
                Hello Tram
                <div class="todoContainer">

                    <ul class="list-group" id="pendingList">
                        {this.state.pending.map(item => (
                            <li key={item.id} class="list-group-item">
                                {item.value} 
                                <button  onClick={() => this.removeItem(item.id)}><i class="fa fa-remove" title={'Remove'}></i></button>
                            </li>
                        ))}

                    </ul>

                    <textarea 
                        class="inputItems"
                        placeholder="..." 
                        onChange={this.onChangeValue}>
                    </textarea> 


                    <button  id="addToDo" onClick={this.addItem}>Add</button>
                </div>

            </div>

        );
    }
}

export default MyPage;
